import React, { Component } from 'react';
import Navbar from "../navbar/index"
import "./style.css";

class Wrapper extends Component {
    constructor() {
        super(props);
    }
    state = {
        theme: "dark-theme"
    }
    render() {
        return (
            <div id="wrapper" className={this.state.theme}>
                <Navbar />
                <button onClick={this.theming}>turn on</button>
            </div>
        );
    }

    theming = this.theming.bind(this);

    theming() {
        if(this.state.theme === "dark-theme") {
             this.setState({
                theme: "light-theme"
             });
        } else {
            this.setState({
                theme: "dark-theme"
             });
        }
    }

}

export default Wrapper;

